INSERT INTO `cnslt` (`kid`, `proj`, `inp_fr`, `inp_to`, `inp_show`, `spprt_show`, `spprt_fr`, `spprt_to`, `spprt_ct`, `vot_fr`, `vot_to`, `vot_show`, `vot_expl`, `vot_res_show`, `summ_show`, `follup_show`, `ord`, `titl`, `titl_short`, `titl_sub`, `img_file`, `img_expl`, `expl`, `ln`, `adm`, `public`) VALUES

  (8,'sd,ej','2012-09-04 00:00:00','2012-11-18 23:59:00','y','n','2012-09-04 00:00:00','2012-11-18 23:59:00',0,'2012-12-04 07:00:00','2013-02-28 23:59:00','y','','y','n','n',8,'Orientierung¿ Los!','Orientierung¿ Los!','','la_dina_photocase.de.png','la dina.PHOTOCASE.com','&lt;h1 style=&quot;text-align: center;&quot;&gt;Orientierung&amp;iquest; Los!&lt;/h1&gt;\r\n\r\n&lt;p&gt;&lt;em&gt;&amp;Uuml;berg&amp;auml;nge &amp;ndash; &amp;Uuml;berbr&amp;uuml;ckungszeit &amp;ndash; Voll der &amp;Uuml;berflieger! &amp;ndash; &amp;Uuml;bung macht den Meister &amp;ndash; &amp;uuml;ber den Horizont hinaus &amp;ndash; &amp;uuml;ber etwas hinwegkommen &amp;ndash; &amp;uuml;ber etwas stolpern &amp;ndash; sich &amp;uuml;berwinden &amp;ndash; &amp;uuml;ber sich hinauswachsen. Ihr habt bestimmt schon so einige pers&amp;ouml;nliche &amp;Uuml;berg&amp;auml;nge erlebt!&lt;/em&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Das sind Lebensphasen, die nicht immer einfach zu meistern sind. Oft passieren sie ganz von selbst: Man wird &amp;auml;lter, bringt die Pubert&amp;auml;t hinter sich, koppelt sich ab vom Elternhaus und wird &amp;bdquo;erwachsen&amp;ldquo;. Viele &amp;Uuml;berg&amp;auml;nge werden auch von au&amp;szlig;en strukturiert und vorgegeben, wie z.B. der Wechsel von der Grundschule in die weiterf&amp;uuml;hrende Schule und von dieser in die Ausbildung oder ins Studium. Aber das ist noch lange nicht alles. Dazwischen werden Freiwilligendienste, Praktika, Neben- oder Minijobs oder Auslandsaufenthalte genutzt um Wartezeiten zu &amp;uuml;berbr&amp;uuml;cken oder sich eine Auszeit zum Verschnaufen und Orientieren zu nehmen. Die Freir&amp;auml;ume, die noch bleiben, verbringen viele damit, dar&amp;uuml;ber zu sinnieren, was sie mal werden wollen und wie sie das schaffen k&amp;ouml;nnen. &amp;ndash; Alles ganz sch&amp;ouml;n verplant, oder?&lt;/p&gt;\r\n\r\n&lt;p&gt;Wenn einem die Zukunftsideen fehlen oder die &amp;Uuml;berg&amp;auml;nge sich holpriger als gedacht herausstellen, kann man auf die Hilfe von Eltern, Freunden, Projekten zur Berufsorientierung oder auf die Unterst&amp;uuml;tzung von Arbeitsagentur und Schule zur&amp;uuml;ckgreifen. Doch wie hilfreich sind diese Angebote wirklich? Die Gestaltung von &amp;Uuml;berg&amp;auml;ngen bzw. &amp;bdquo;Weichenstellungen f&amp;uuml;r die Jugend&amp;ldquo; war das Leitthema des 3. Fachforums zur Entwicklung einer Eigenst&amp;auml;ndigen Jugendpolitik. Fachleute aus Politik und Gesellschaft haben sich hier &amp;uuml;berlegt, welche Rahmenbedingen f&amp;uuml;r die &amp;Uuml;bergangsgestaltung wichtig sind und sich mit der Rolle unterschiedlicher &amp;bdquo;Player&amp;ldquo; in diesem Bereich auseinandergesetzt. Gleichzeitig besch&amp;auml;ftigt das Thema auch die Fachleute, die die sogenannte EU-Jugendstrategie in Deutschland umsetzen. Bei ihnen steht das Lernen im Ausland im Mittelpunkt der Diskussionen.&lt;/p&gt;\r\n\r\n&lt;p&gt;Welchen Einfluss Schule, Gleichaltrige, au&amp;szlig;erschulische Bildungsorte, Auslandserfahrungen und die Wirtschaft auf eure Lebensgestaltung haben und welche Bedingungen n&amp;ouml;tig sind, um &amp;bdquo;Orientierungslosigkeit&amp;ldquo; zu vermeiden - dazu k&amp;ouml;nnt ihr euch jetzt positionieren!dfasf&lt;br /&gt;\r\n&lt;br /&gt;\r\n&amp;nbsp;&lt;/p&gt;\r\n','de',0,'y');
COMMIT;

