INSERT INTO `inpt` (`tid`, `qi`, `kid`, `thes`, `expl`, `uid`, `when`, `block`, `user_conf`, `vot`, `typ`, `spprts`, `votes`, `pts`, `rel_tid`, `tg_nrs`, `notiz`, `confirm_key`) VALUES

  (2100,775195,8,'Schüler in den Abschlussklassen brauchen die Unterstützung von persönlichen Mentoren zur Gestaltung ihres beruflichen Werdegangs.','Jugendliche in der sensiblen Übergangsphase Schule-Beruf brauchen feste Ansprechpartner, die sie dabei unterstützen, ihre individuellen Interessen und Fähigkeiten zu identifizieren, zu filtern und den Weg hin zur Ausbildung/zum Studium mitzugestalten. Dabei hilft es, wenn diese Ansprechpartner nicht aus der Familie und dem Freundeskreis stammen, sondern als \\&quot;neutrale\\&quot; Akteure den Jugendlichen begleiten. Ziel des außerschulischen \\&quot;Komplizen-Programms\\&quot; ist es, den Schülern in den Abschlussklassen durch junge Berufstätige als ehrenamtliche Mentoren auf die Sprünge zu helfen - durch regelmäßigen 1:1-Austausch und Unternehmensbesichtigungen.',25619,'2012-09-24 11:55:23','n','c','u','p',0,0,0,'3117','324,146,309','',''),
  (2101,775196,8,'Außerschulische Orientierungspraktika sind entscheidend für erste Einblicke und Erfahrungen in die Berufswelt - es muss jedoch die richtige Wahl getroffen werden. Dabei können \\&quot;Arbeitsplatzbesichtigungen\\&quot; helfen.','Im Mentoringprogramm der Komplizen bieten \\&quot;Arbeitsplatzbesichtigungen\\&quot; als Orientierungstage wichtige Praxiseinblicke: Die teilnehmenden Schüler lernen im Laufe des Programms 10 verschiedene Arbeitsplätze kennen, um konkrete Erfahrungen und Eindrücke des Berufsalltags zu sammeln und diese im Anschluss mit dem eigenen Mentor zu reflektieren. Der Vorteil: die Schüler \\&quot;erleben\\&quot; ein breites Spektrum an Berufsbildern, die sie interessieren. Daraus können sie Prioritäten setzen, sich den für sie interessantesten Arbeitgeber heraussuchen und sich dort für ein vertieftes, mehrwöchiges Orientierungspraktikum in den Ferien bewerben.',25619,'2012-09-24 12:04:36','n','c','n','p',0,0,0,'3132,3145','230,391,282','',''),
  (2102,775197,8,'Netzwerke sind das A und O in der Berufsorientierung und bieten wichtige Anlaufstellen, v.a. für benachteiligte Schüler.','Das Mentoring-Programm zur Persönlichkeitsentwicklung und Berufsorientierung der Komplizen bietet den Schülern neben der 1:1-Beratung und den Arbeitsplatzbesichtigungen auch Zugang zu Bildungs- und Berufsnetzwerken. Dieser ist, wie auch die Teilnahme am Programm insgesamt, für alle Schüler kostenfrei. Die virtuelle Komplizen-Plattform ermöglicht darüber hinaus den Kontakt zu allen am Programm beteiligten Mentoren und Unternehmen und wird kontinuierlich erweitert und weiter vernetzt. Die Schüler haben damit die Möglichkeit, auch über die Programmteilnahme hinaus über das Netzwerk Kontakte aufzubauen und zu pflegen.',25619,'2012-09-24 12:12:04','n','c','n','p',3,0,0,'3213','226,146','',''),
  (2103,775198,8,'Der Austausch zwischen Schulen und regionalen Unternehmen wird angesichts des Fachkräftemangels und der steigenden Zahl von Ausbildungs- und Studiumsabbrechern immer dringlicher - und kann ein bedeutsamer Standortfaktor sein.','Dabei können die zunehmende Ausbreitung der Ganztagsschulen und ihre strukturellen Gestaltungsmöglichkeiten hilfreich sein: es gibt mehr Zeit, Schulen und regionale Unternehmen zu vernetzen und zu einem gezielten Austausch anzuregen. Indem junge Berufstätige sich (z.B. 1x/Monat am Nachmittag) in der Berufsorientierung als persönliche Begleiter von Jugendlichen engagieren, entsteht ein wichtiger Ansatzpunkt für eine verbesserte Interaktion zwischen Schulen und Wirtschaft. Denn auch die Mentoren \\&quot;lernen\\&quot; von den Jugendlichen und sollten ihre Sorgen, Ängste, Hoffnungen und Visionen in die Unternehmen hineintragen und in entspr. Kanälen verarbeiten und rückkoppeln.',25619,'2012-09-24 13:05:15','n','c','n','p',0,0,0,'3282,3283','369,146,304','',''),
  (2104,775199,8,'Vorbilder spielen eine entscheidende Rolle in der Persönlichkeitsentwicklung eines Jugendlichen.','Das Komplizen-Programm hat eindrucksvoll gezeigt, dass Jugendliche besonders profitieren, wenn sie von (auch) jungen, aber erfahrenen Menschen unterstützt werden. Die teilnehmenden Schüler sind zwischen 14-17 Jahre alt, ihre Mentoren zwischen 25-39 Jahren. Die Mentoren haben also bereits ihre ersten Berufserfahrungen hinter sich, gehören aber nicht der Elterngeneration an und können somit als älterer Bruder oder Freund agieren. Als \\&quot;Komplizen\\&quot; gestalten gemeinsam die beruflichen Perspektiven des Schülers - auf Augenhöhe, aber mit klar verteilten Rollen.',25619,'2012-09-24 13:10:42','n','c','n','p',2,0,0,'','149','',''),
  (2105,775201,8,'Auslandserfahrungen sind für Jugendliche besonders bereichernd und öffnen in einer wichtigen Entwicklungsphase den Blick für die weite Welt und ihre (beruflichen) Chancen außerhalb des vertrauten Umfelds. Lernerfahrungen im Ausland können durch die interaktiven Möglichkeiten des Internets angestoßen werden.','Schulklassen knüpfen Partnerschaften mit Schulklassen im Ausland. Es werden kleine Teams gebildet, die sich regelmäßig über Skype austauschen und gemeinsam über mehrere Monate an einem Bildungsprojekt arbeiten. Zum Abschluss kann eine Reise zur Partnerschule erfolgen; die Schüler werden in den Familien der Partnerschüler untergebracht. Das persönliche Kennenlernen der Teams bietet die Basis für weitere, grenzüberschreitende Projekte. Im optimalen Fall entstehen Freundschaften, die auch außerhalb der Schulzeiten vertieft und gepflegt werden -  für beide Seiten ein Gewinn.',25619,'2012-09-24 13:25:12','n','c','n','p',0,0,0,'','416,157,146','',''),
  (2106,775201,8,'Virtuelle Berufsorientierung ermöglicht grenzüberschreitenden Austausch.','Die Komplizen entwickeln derzeit ein virtuelles Mentoring-Programm (e-Komplizen), das allen Schülern auch außerhalb der Ballungsräume die Teilnahme ermöglicht, denn: der Austausch mit dem Mentor und die Arbeitsplatzbesichtigungen erfolgen Online. Der Austausch ist damit standortunabhängig und kann auch grenzüberschreitend in bspw. deutsch-französischen Teams erfolgen. Schüler mit besonderem Interesse an Frankreich könnten mit Hilfe ihres französichem Mentors die dortigen Arbeitsmöglichkeiten und -perspektiven erkunden und gleichzeitig ihre Sprachkenntniss verbessern.',25619,'2012-09-24 13:25:12','n','c','n','p',0,0,0,'','157,246','',''),
  (2108,775195,8,'Bevor man auf die Fähigkeiten eingehen kann, muss man erst einmal heraus finden, welche Fähigkeiten besitze ich? Wo brauche ich noch Förderung? Was ist nur Hobby und was wird zum Beruf? Kann ich später davon leben? Wie sind in dem Berufsfeld meine Karrierechancen? Ist mein zukünftiger Arbeits- bzw Ausbildungsplatz sicher? Wer ha','',997,'2012-10-04 14:28:39','n','u','n','p',0,0,0,'3074','277,299','',''),
