INSERT INTO `ml_def` (`refnm`, `kid`, `proj`, `ln`, `subj`, `txt`, `html`, `expl`, `head`, `foot`) VALUES
  ('inpt_conf', 0, 'sd', 'de', 'Konsultation „{{CNSLT_TITLE_SHORT}}“: Bitte Einträge bestätigen', 'Hallo {{USER}},\r\n\r\ndanke für die Beteiligung an der Konsultation „{{CNSLT_TITLE}}“ im Rahmen des Strukturierten Dialogs. Unten findest Du eine Übersicht Deiner/Eurer Antworten auf die Konsultationsfragen.\r\n\r\nUm sicherzustellen, dass die Einträge nicht von einem Spamversender kommen, bitten wir Dich um die Bestätigung der Eingaben über folgenden Link.\r\n\r\n{{CONFIRMLINK}}\r\n\r\nFalls sich der Bestätigungslink nicht anklicken lässt oder ein Teil des Links abgeschnitten wurde, kopiere bitte den gesamten Link und füge ihn in die Adressleiste Deines Browsers ein. Drücke anschließend die Eingabetaste.\r\n\r\nFalls du die Beiträge nicht authorisieren kannst, weise sie bitte mittels dieses Links ab:\r\n\r\n{{REJECTLINK}}\r\n\r\nDas Sammeln von Beiträgen und damit die erste Konsultationsphase endet am {{INPUT_TO}}. Danach werden wir alle Teilnehmenden bitten, aus den gesammelten Beiträgen diejenigen auszuwählen, die ihnen am wichtigsten sind. Dazu erhältst Du/erhaltet Ihr eine E-Mail mit weiteren Informationen von uns.\r\n\r\n\r\nBei Rückfragen stehen Dir Ann-Kathrin Fischer und Tim Schrock in der Koordinierungsstelle für den Strukturierten Dialog gern zur Verfügung. Einfach anmailen (sd@dbjr.de) oder anrufen unter 030. 400 40-443.\r\n\r\nMit freundlichen Grüßen\r\nEure Koordinierungsstelle für den Strukturierten Dialog\r\n\r\n==============================================================================\r\n\r\nÜbersicht über Eure Beiträge zur Konsultation\r\n„{{CNSLT_TITLE}}“\r\n\r\n==============================================================================\r\n\r\n{{USER_INPUTS}}\r\n', '', '{{USER}} {{CNSLT_TITLE}} {{CNSLT_TITLE_SHORT}} {{INPUT_TO}} {{CONFIRMLINK}} {{REJECTLINK}} {{USER_INPUTS}}', 'n', 'y');